@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

html {

  /* Colors */
  --color-accent: #ffc329;

  /* Default property values */
  --background: white;
  --color: #222;
  --padding: 0; 
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2); 
  --border-radius: 8px; 
  --max-width: 100%;
  --border-color: #CBCACE;
  --transition-time: 0.1s;
  --transition: var(--transition-time) color,
    var(--transition-time) background-color,
      var(--transition-time) border-color,
        var(--transition-time) text-decoration-color,
          var(--transition-time) box-shadow, var(--transtion-time) transform;
}

.primo-page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1.25rem;
  background: var(--background);
}

.primo-section .primo-content {
  max-width: var(--max-width);
  margin: 0 auto;
  padding: var(--padding);

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  p {
    padding: 0.25rem 0;
    line-height: 1.5;
  }

  a {
    text-decoration: underline;
    &:hover {
      color: var(--color-accent);
    }
  }

  h1 {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
  }

  h2 {
    font-size: 2.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
  }

  h3 {
    font-size: 1.75rem; 
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container {
  max-width: var(--max-width, 1200px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

@media screen and (max-width: 700px) {
  .heading {
    font-size: 2rem;
  }
}

.section-container {
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  color: #fff;
  background-size: cover;
}
.content {
  display: flex;
  flex-direction: column;
  padding: 1rem;
  height: min(400px, 80vh);
  place-content: center;
  text-align: center;
  gap: 0.5rem;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAAXNSR0IArs4c6QAAABZJREFUGFdjZEADjFD+fyjNiFMArhEARd4CBSFQSzYAAAAASUVORK5CYII=) rgba(0,0,0, 0.3);

  .social-links {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
  }

  a {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    &:hover {
      color: var(--color-accent)
    }
  }
}

@media (min-width: 700px) {
  .section-container {
    gap: 5rem;
  }
  .section-container .content {
    gap: 1rem;
  }
}
